export class ProfileMapper {
  static map(profile, quote = {}) {
    const {
      usd_market_cap = null, usd_global_volume_24h, usd_low, usd_high, circulating_supply, total_supply
    } = quote

    return {
      ...profile,
      ...{
        marketCap: usd_market_cap,
        globalVolume: usd_global_volume_24h,
        priceLow: usd_low,
        priceHigh: usd_high,
        circulatingSupply: circulating_supply,
        totalSupply: total_supply
      }
    }
  }
}
