export function resolutionParam(resolution) {
  let url = 'timespan=minute'

  if (resolution === 'D') {
    url = 'timespan=daily'
  } else if (resolution >= 60) {
    url = 'timespan=hourly'
  }

  return url
}

export function fromParam(from) {
  if (from) {
    return `&from=${from}`
  }

  return ''
}

export function toParam(to) {
  if (to) {
    return `&to=${to}`
  }

  return ''
}

export function limitParam(l) {
  let limit = 2000
  if (l) {
    limit = l
  }

  return `&limit=${limit}`
}

export function orderParam(dir) {
  if (dir) {
    return `&order=${dir}`
  }

  return ''
}
