export class MockAxios {
  static async post(url, params) {
    console.log('***MOCK POST: ', url)

    let response
    if (params.userId !== '1') {
      response = { status: 200, data: { token: 'fake-token' } }
    } else {
      response = { status: 401 }
    }
    console.log('***MOCK data: ', url, response)

    return Promise.resolve(response)
  }
}
