export class MockAxios {
  static async delete(url, params) {
    console.log('***MOCK DELETE: ', url, params)
    const response = { status: 204, data: { } }

    return Promise.resolve(response)
  }

  static async get(url, params) {
    console.log('***MOCK GET: ', url, params)

    const response = { status: 200, data: {} }
    if (/.+accounts\?exchangeUserId=\d+/.test(url)) {
      response.data = (await import('./accounts.json')).default
    } else {
      console.log('***MOCK MISSING')
    }

    return Promise.resolve(response)
  }

  static async post(url, params) {
    console.log('***MOCK POST: ', url, params)

    let response

    if (params.token === 'bad-token') {
      response = { status: 401 }
      console.log('***MOCK data: ', url, response)
      return Promise.resolve(response)
    }

    if (params.exchangeUserId !== undefined) {
      response = { status: 201, data: { } }
    } else {
      response = { status: 401 }
    }
    console.log('***MOCK data: ', url, response)

    return Promise.resolve(response)
  }
}
